import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  fetchPosts,
  selectPosts,
  deletePost,
  addPost as addPostAction,
  updatePost as updatePostAction,
} from "../features/posts/postsSlice";

const UserDashboard = () => {
  const dispatch = useDispatch();
  const posts = useSelector(selectPosts);
  const [formData, setFormData] = useState({ title: "", content: "" });
  const [editingPostId, setEditingPostId] = useState(null);

  useEffect(() => {
    dispatch(fetchPosts());
  }, [dispatch]);

  const handleDelete = (postId) => {
    dispatch(deletePost(postId));
  };

  const handleAddPost = () => {
    dispatch(addPostAction(formData.title, formData.content));
    setFormData({ title: "", content: "" });
  };

  const handleUpdatePost = (postId) => {
    setEditingPostId(postId);
    const postToUpdate = posts.find((post) => post.id === postId);
    if (postToUpdate) {
      setFormData({ title: postToUpdate.title, content: postToUpdate.content });
    }
  };

  const handleFormChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    dispatch(updatePostAction(editingPostId, formData));
    setFormData({ title: "", content: "" });
    setEditingPostId(null);
  };

  return (
    <div>
      <h2>User Dashboard</h2>
      <div>
        <input
          type="text"
          placeholder="Title"
          value={formData.title}
          name="title"
          onChange={handleFormChange}
        />
        <textarea
          placeholder="Content"
          value={formData.content}
          name="content"
          onChange={handleFormChange}
        ></textarea>
        <button onClick={handleAddPost}>Add Post</button>
      </div>
      <ul>
        {posts.map((post) => (
          <li key={post.id}>
            <h3>{post.title}</h3>
            <p>{post.content}</p>
            {editingPostId === post.id ? (
              <form onSubmit={handleFormSubmit}>
                <input
                  type="text"
                  placeholder="Title"
                  value={formData.title}
                  name="title"
                  onChange={handleFormChange}
                />
                <textarea
                  placeholder="Content"
                  value={formData.content}
                  name="content"
                  onChange={handleFormChange}
                ></textarea>
                <button type="submit">Update</button>
              </form>
            ) : (
              <>
                <button onClick={() => handleUpdatePost(post.id)}>
                  Edit Post
                </button>
                <button onClick={() => handleDelete(post.id)}>
                  Delete Post
                </button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UserDashboard;
