            <Route path="/admin-dashboard" element={role==="admin"?<AdminDashboard />:<Navigate to="/"/>} />
            <Route path="/user-dashboard" element={role==="regular"?<UserDashboard />:<Navigate to="/"/>} />
      </Routes>
