  );
};

export default App; */


const router = createBrowserRouter([

  {
    path:'/',
    element: <DefaultLayout />,
    children:[
      {
        path:'/users',
        element:<AdminDashboard/>
      },
      {
        path:'/posts',
        element:<UserDashboard/>
      },
    ]
  },
  {
    path:'/',
    element: <GuestLayout />,
    children:[
      {
        path:'/login',
        element:<LoginForm />
      },
      {
        path:'/register',
        element:<RegistrationForm />
      },
    ]
  },


  {
    path:'*',
    element:<NotFound />
  }
])

export default router;
