// App.js
import {useSelector } from "react-redux";
import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import LoginForm from "./components/LoginForm";
import RegistrationForm from "./components/RegistrationForm";
import AdminDashboard from "./components/AdminDashboard";
import UserDashboard from "./components/UserDashboard";
import { selectIsLoggedIn } from "./features/auth/authSlice";
import { selectRole } from "./features/auth/authSlice";
import { Navigate,Outlet } from "react-router-dom";


const PrivateRoutes = () => {
  const isLoggedIn = useSelector(selectIsLoggedIn); // Get login status
  return (
    isLoggedIn ? <Outlet/> : <Navigate to='/login'/>
  )
}

const App = () => {
  const isLoggedIn = useSelector(selectIsLoggedIn); // Get login status

  const role = useSelector(selectRole); 

  return (
    <Router>
      <Routes>
        <Route element={<PrivateRoutes/>}>
            <Route path="/register" element={<RegistrationForm />} />
            <Route path="/admin-dashboard" element={role==="admin"?<AdminDashboard />:<Navigate to="/logout"/>} />
            <Route path="/user-dashboard" element={role==="regular"?<UserDashboard />:<Navigate to="/logout"/>} />
        </Route>
        <Route path="/login" element={isLoggedIn?<Navigate to="/logout"/>:<LoginForm /> } />
        <Route path="/logout" Navigate to="/"/>
      </Routes>
    </Router>
  );
};

export default App;
