const router = createBrowserRouter([

  {
    path:'/login',
    element:<LoginForm />
  },
  {
    path:'/register',
    element:<RegistrationForm />
  },
  {
    path:'/users',
    element:<AdminDashboard/>
  },
  {
    path:'/posts',
    element:<UserDashboard/>
  },
  {
    path:'*',
    element:<NotFound />
  }
])

export default router;
